import { useMegaWidget } from "../../hooks/use-mega-widget"
import type { MegaWidgetItem } from "../../lib/mega-widget"
import { WidgetBody } from "./_components/widget-body"
import { WidgetFooter } from "./_components/widget-footer"
import { WidgetHeader } from "./_components/widget-header"

export type MegaWidgetProps = {
  title?: string
  items?: MegaWidgetItem[]
}

export function MegaWidget({ title = "Mega Widget", items }: MegaWidgetProps) {
  const { count, increment, reset } = useMegaWidget()

  const resolvedItems: MegaWidgetItem[] =
    items ??
    [
      { id: "a", label: "First internal row" },
      { id: "b", label: "Second internal row" },
      { id: "c", label: "Third internal row" },
    ]

  return (
    <section className="rounded-md border bg-background">
      <WidgetHeader title={title} count={count} />
      <WidgetBody items={resolvedItems} />
      <WidgetFooter onIncrement={increment} onReset={reset} />
    </section>
  )
}

export type WidgetHeaderProps = {
  title: string
  count: number
}

export function WidgetHeader({ title, count }: WidgetHeaderProps) {
  return (
    <header className="flex items-center justify-between border-b p-4">
      <div>
        <div className="text-sm font-medium">{title}</div>
        <div className="text-xs text-muted-foreground">Internal parts live in a subfolder</div>
      </div>
      <div className="text-xs text-muted-foreground">Clicks: {count}</div>
    </header>
  )
}

import type { MegaWidgetItem } from "../../../lib/mega-widget"
import { WidgetRow } from "./widget-row"

export type WidgetBodyProps = {
  items: MegaWidgetItem[]
}

export function WidgetBody({ items }: WidgetBodyProps) {
  return (
    <div className="p-4">
      <div className="space-y-2">
        {items.map((item) => (
          <WidgetRow key={item.id} item={item} />
        ))}
      </div>
    </div>
  )
}

import type { MegaWidgetItem } from "../../../lib/mega-widget"

export type WidgetRowProps = {
  item: MegaWidgetItem
}

export function WidgetRow({ item }: WidgetRowProps) {
  return (
    <div className="flex items-center justify-between rounded-md bg-muted px-3 py-2 text-sm">
      <span>{item.label}</span>
      <span className="text-xs text-muted-foreground">#{item.id}</span>
    </div>
  )
}

export type WidgetFooterProps = {
  onIncrement: () => void
  onReset: () => void
}

export function WidgetFooter({ onIncrement, onReset }: WidgetFooterProps) {
  return (
    <footer className="flex items-center justify-end gap-2 border-t p-4">
      <button
        type="button"
        className="rounded-md border bg-background px-3 py-2 text-sm"
        onClick={onReset}
      >
        Reset
      </button>
      <button
        type="button"
        className="rounded-md bg-primary px-3 py-2 text-sm text-primary-foreground"
        onClick={onIncrement}
      >
        Increment
      </button>
    </footer>
  )
}

import * as React from "react"

export function useMegaWidget() {
  const [count, setCount] = React.useState(0)

  const increment = React.useCallback(() => setCount((c) => c + 1), [])
  const reset = React.useCallback(() => setCount(0), [])

  return { count, increment, reset }
}

export type MegaWidgetItem = {
  id: string
  label: string
}
